mapper.update:
		UPDATE ReviewBoard SET 
                rv_name= #{rv_name},
                rv_content= #{rv_content}
                WHERE rv_no= #{rv_no}

mapper.select:
		SELECT R.*,U.U_NICKNAME,
      		(SELECT COUNT(*) FROM RvLikes WHERE RV_NO =R.rv_no) LIKECNT 
      	  FROM ReviewBoard R, USERS U WHERE R.U_ID=U.U_ID AND rv_no= #{rv_no}

mapper.update:
		UPDATE ReviewBoard SET 
                rv_status= 0
                WHERE rv_no= #{rv_no}
