mapper.update:
UPDATE Users SET u_pw=#{u_pw},
                 u_nickname=#{u_nickname},
                 u_phone=#{u_phone},
                 u_tel=#{u_tel},
                 u_email= #{u_email},
                 u_addr= #{u_addr},
                 u_birth= #{u_birth}

				,u_image= #{u_image}

           WHERE u_id=#{u_id}
